LHJoint* LHSprite::jointWithUniqueName(const std::string& name){
    
    CCArray* list = jointList();
    for(int i = 0; i< list->count(); ++i){
        LHJoint* jt = (LHJoint*)list->objectAtIndex(i);
        
        if(jt->getUniqueName() == name){
            return jt;
        }
    }
    return NULL;
}
