void LHSprite::makeDynamic(){
    
    if(body == NULL)
    {
        this->createBodyFromDefaultValuesWithType(b2_dynamicBody);
        this->createFixturesFromInfoOnBody();
        return;
    }
    
    body->SetType(b2_dynamicBody);    
}
void LHSprite::makeStatic(){
    if(body == NULL){
        this->createBodyFromDefaultValuesWithType(b2_staticBody);
        this->createFixturesFromInfoOnBody();
        return;
    }
    
    body->SetType(b2_staticBody);
}
void LHSprite::makeKinematic(){
    if(body == NULL){
        this->createBodyFromDefaultValuesWithType(b2_kinematicBody);
        this->createFixturesFromInfoOnBody();
        
        return;
    }
    
    body->SetType(b2_kinematicBody);
}


void LHSprite::makeNoPhysics(){
    if(body == NULL)
        return;
    
    this->removeBodyFromWorld();
}

void LHSprite::setSensor(bool val, const std::string fixtureName){
   
    if(body == NULL)return;
    
    b2Fixture* fix = body->GetFixtureList();
    
    while (fix) {
        LHFixture* lhFix = (LHFixture*)(fix->GetUserData());
        if(LHFixture::isLHFixture(lhFix))
        {
            if(lhFix->getFixtureName() == fixtureName){
                fix->SetSensor(val);
                return;
            }
        }
        fix = fix->GetNext();
    }
}

void LHSprite::setSensor(bool val, int fixtureID){
    
    if(body == NULL)return;
    
    b2Fixture* fix = body->GetFixtureList();
    
    while (fix) {
        LHFixture* lhFix = (LHFixture*)(fix->GetUserData());
        
        if(LHFixture::isLHFixture(lhFix))
        {
            if(lhFix->getFixtureID() == fixtureID){
                fix->SetSensor(val);
            }
        }
        fix = fix->GetNext();
    }
}

void LHSprite::setSensor(bool val){
    if(body == NULL)return;
    b2Fixture* fix = body->GetFixtureList();
    while (fix) {
        fix->SetSensor(val);
        fix = fix->GetNext();
    }
}

bool LHSprite::hasContacts(){
    if(body == NULL)
        return false;
    
    b2ContactEdge* edge = body->GetContactList();
    if(NULL != edge){
        return true;
    }
    return false;
}

CCArray* LHSprite::contactSprites(){
    
    if(body == NULL)
        return NULL;
    
    b2ContactEdge* edge = body->GetContactList();
    if(NULL == edge){
        return NULL;
    }
    
    
#if COCOS2D_VERSION >= 0x00020000
    CCArray* array = CCArray::create();
#else
    CCArray* array = CCArray::array();
#endif
    
    while (edge != NULL) {
        
        b2Body* contact_body = edge->other;
        
        if(contact_body){
            
            LHSprite* spr = LHSprite::spriteForBody(contact_body);
            if(spr){
                array->addObject(spr);
            }
        }
        edge = edge->next;
    }
    
    return array;
}

CCArray* LHSprite::contactBeziers(){
    
    if(body == NULL)
        return NULL;
    
    b2ContactEdge* edge = body->GetContactList();
    if(NULL == edge){
        return NULL;
    }
    
#if COCOS2D_VERSION >= 0x00020000
    CCArray* array = CCArray::create();
#else
    CCArray* array = CCArray::array();
#endif


    while (edge != NULL) {
        
        b2Body* contact_body = edge->other;
        
        if(contact_body){
            
            LHBezier* bez = LHBezier::bezierForBody(contact_body);
            if(bez){
                array->addObject(bez);
            }
        }
        edge = edge->next;
    }
    
    return array;
}

LHFixture* LHSprite::lhFixtureOfContactingSpriteWithTag(int otherTag)
{    
    if(body == NULL)
        return NULL;
    
    b2ContactEdge* edge = body->GetContactList();
    if(NULL == edge){
        return NULL;
    }
    
    while (edge != NULL) {
        
        b2Body* contact_body = edge->other;
        
        if(contact_body){
            
            LHSprite* spr = LHSprite::spriteForBody(contact_body);
            if(spr->getTag() == otherTag)
            {
                b2Contact* contact = edge->contact;
                
                b2Fixture* fixA = contact->GetFixtureA();
                b2Fixture* fixB = contact->GetFixtureB();
                
                //find other body
                
                if(fixA->GetBody() == contact_body){
                    
                    LHFixture* lhFix = LHFixture::fixtureForb2Fixture(fixA);
                    return lhFix;
                    
                }
                else if(fixB->GetBody() == contact_body){
                    
                    LHFixture* lhFix = LHFixture::fixtureForb2Fixture(fixB);
                    return lhFix;
                }
            }
        }
        edge = edge->next;
    }
    
    return NULL;
}

